<template>    <div class="filter">        <button class="btn btn-slim filter-btn" @click.prevent="toggleFilters">Фильтр</button>        <ul :class="{show:showFilters}" class="filter__options">            <li :class="{active:isActive(index)}" v-for="(filter,index) in filters"><a href="#" @click.prevent="addFilter(index)">{{filter}}</a></li>        </ul>    </div></template><script>    module.exports = {        methods: {            isActive:function(indexEl){                store.state.filters.forEach((currentValue, index, array)=>{                     if (index==indexEl){                         return true;                     }else{                         return false;                     }                });            },            addFilter:function(index){                store.commit('addFilter',{'value':index});            },            toggleFilters:function(){                if (this.showFilters){                    this.showFilters=0;                }else{                    this.showFilters=1;                }            }        },        created() {        },        data() {            return {                showFilters: 0            }        },        ready: function () {        },        props: ['filters'],        mounted: function () {            var reference = document.querySelector('.filter');            var popper = document.querySelector('.filter__options');            var anotherPopper = new Popper(                reference,                popper,                {                    // popper options here                }            );        },        computed: {        }    }</script>